	var text_cow = '\n<p>\u5728\u53E4\u4EE3\u6C11\u95F4\uFF0C\u5BF9\u4E8E\u5DE5\u5320\u90FD\u662F\u975E\u5E38\u5C0A\u656C\u7684\uFF0C\u5BB6\u91CC\u5EFA\u623F\u5B50\uFF0C\u505A\u7AF9\u5668\uFF0C\u505A\u6728\u5DE5\uFF0C\u8981\u8BF7\u6CE5\u5320\uFF0C\u7BFE\u5320\uFF0C\u6728\u5320\u5230\u5BB6\u91CC\u6765\u5E72\u6D3B\uFF0C\u90FD\u5F97\u597D\u751F\u62DB\u5F85\uFF0C\u996D\u83DC\u8BB2\u7A76\u5012\u662F\u6CA1\u6709\uFF0C\u4F46\u662F\u96C7\u4E3B\u90FD\u4F1A\u6781\u5C3D\u4E30\u76DB\u7684\u6B3E\u5F85\u3002\u56E0\u4E3A\u6C11\u95F4\u90FD\u77E5\u9053\uFF0C\u4E00\u65E6\u5DE5\u5320\u8981\u5BB3\u4EBA\uFF0C\u8EB2\u90FD\u8EB2\u4E0D\u4E86\u3002</p>\n<p>\u65E9\u671F\u7684\u5DE5\u5320\uFF0C\u4E0D\u50CF\u73B0\u5728\u5168\u662F\u673A\u68B0\u5316\u751F\u4EA7\uFF0C\u4E0D\u9700\u8981\u6536\u5F92\u5F1F\u4E86\u3002\u90A3\u4E2A\u65F6\u5019\u662F\u8981\u6536\u5F92\u5F1F\u7684\uFF0C\u800C\u4E14\u5F92\u5F1F\u5F97\u8DDF\u7740\u5E08\u5085\u5B66\u597D\u51E0\u5E74\u3002</p>\n<p>\u5E08\u5085\u5728\u8003\u5BDF\u5F92\u5F1F\u5B66\u827A\u80FD\u529B\u540C\u65F6\uFF0C\u4E5F\u8981\u8003\u5BDF\u5F92\u5F1F\u7684\u4EBA\u54C1\uFF0C\u9047\u5230\u4E86\u597D\u7684\u5F92\u5F1F\uFF0C\u5E08\u5085\u4F1A\u4F20\u6388\u4E00\u4E24\u5957\u4FDD\u547D\u7684\u4E1C\u897F\uFF0C\u636E\u8BF4\u662F\u8305\u5C71\u9053\u672F\u3002</p>\n<p>\u4F46\u662F\u4E0D\u662F\u6BCF\u4E2A\u5F92\u5F1F\u90FD\u6709\uFF0C\u4E00\u822C\u5FC3\u672F\u4E0D\u6B63\u7684\u4E5F\u4E0D\u4F1A\u6559\uFF0C\u8FD9\u6837\u4E0D\u4F46\u5BB3\u4EBA\uFF0C\u4E5F\u4F1A\u5BB3\u4E86\u5F92\u5F1F\u81EA\u5DF1\u3002</p>\n<p>\u636E\u8BF4\u4F7F\u7528\u8FD9\u6837\u7684\u9053\u672F\u5BB3\u4EBA\uFF0C\u5982\u679C\u88AB\u4EBA\u7834\u4E86\uFF0C\u6216\u8005\u5BB3\u4EBA\u6210\u529F\u4EE5\u540E\uFF0C\u4E5F\u4F1A\u4F24\u53CA\u5B50\u5B59\u540E\u4EE3\uFF0C\u5982\u679C\u6B21\u6570\u591A\u4E86\uFF0C\u8FD8\u6709\u53EF\u80FD\u7EDD\u540E\uFF01</p>\n<p>\u8BDD\u8BF4\u5728\u6297\u65E5\u5E74\u95F4\uFF0C\u9B3C\u5B50\u5357\u4E0B\u6253\u4E0B\u4E86\u6C5F\u897F\uFF0C\u5728\u9760\u8FD1\u6B66\u6C49\u65B9\u5411\uFF0C\u9B3C\u5B50\u8981\u4FEE\u5EFA\u5730\u4E0B\u5DE5\u4E8B\uFF0C\u6240\u4EE5\u5728\u5F53\u5730\u6293\u4E86\u5F88\u591A\u58EE\u4E01\uFF0C\u800C\u4E14\u5728\u6293\u58EE\u4E01\u7684\u65F6\u5019\uFF0C\u5F88\u591A\u4E0D\u613F\u610F\u7684\uFF0C\u90FD\u88AB\u9B3C\u5B50\u7D66\u6740\u4E86\uFF0C\u6240\u4EE5\u9B3C\u5B50\u5728\u5F53\u5730\u6740\u4E86\u4E0D\u5C11\u4EBA\uFF0C\u4ED6\u4EEC\u6293\u5230\u4E86\u4EBA\u4EE5\u540E\uFF0C\u5C31\u53EA\u7BA1\u903C\u7740\u5E72\u6D3B\uFF0C\u5DE5\u4EBA\u4EEC\u662F\u82E6\u4E0D\u582A\u8A00\u3002</p>\n<p>\u5176\u4E2D\u6709\u4E00\u4E2A\u6CE5\u5320\u5DE5\u53EB\u6C49\u751F\uFF0C\u6253\u5C0F\u5B66\u8FD9\u4E2A\uFF0C\u5E2E\u4EBA\u5EFA\u623F\u5B50\uFF0C\u4E5F\u5EFA\u4E8620\u591A\u5E74\u5FEB30\u5E74\u4E86\uFF0C\u672C\u6765\u5BB6\u91CC\u8FD8\u7B97\u53EF\u4EE5\u7684\u3002</p>\n<p>\u4ED6\u7684\u5927\u513F\u5B50\u662F\u4E00\u4E2A\u4E2D\u6821\u519B\u5B98\uFF0C\u4F46\u662F\u65E5\u672C\u4EBA\u5165\u4FB5\u4E2D\u56FD\u4EE5\u540E\uFF0C\u5728\u6DDE\u6CAA\u4F1A\u6218\u727A\u7272\u4E86\uFF0C\u4E8C\u513F\u5B50\u662F\u521A\u521A\u4ECE\u519B\u6821\u6BD5\u4E1A\u51FA\u6765\u7684\u5C09\u5B98\uFF0C\u5728\u5357\u4EAC\u4FDD\u536B\u6218\u4E2D\u727A\u7272\u4E86\uFF01\u4ED6\u8FD8\u6709\u4E00\u4E2A\u5C0F\u513F\u5B50\uFF0C\u672C\u6765\u5408\u8BA1\u5C31\u9760\u8FD9\u4E2A\u5C0F\u513F\u5B50\u517B\u8001\u4E86\uFF0C\u8C01\u77E5\u9053\u9B3C\u5B50\u6253\u8FDB\u4E86\u6C5F\u897F\u4EE5\u540E\uFF0C\u4ED6\u7684\u8FD9\u4E2A\u5C0F\u513F\u5B50\uFF0C\u4E5F\u88AB\u9B3C\u5B50\u7D66\u6740\u4E86\u3002\u800C\u4ED6\u88AB\u5E26\u5230\u4E86\u8FD9\u8FB9\u6765\u4FEE\u5730\u4E0B\u63A9\u4F53\u3002</p>\n<p>\u56E0\u4E3A\u6C49\u751F\u662F\u4E00\u4E2A\u6CE5\u5320\uFF0C\u6240\u4EE5\u9B3C\u5B50\u5C31\u7D66\u4E86\u4ED6\u4E00\u628A\u6CE5\u5200\uFF08\u6CE5\u5320\u7528\u7684\u5200\u5177\uFF0C\u6CA1\u6709\u5F00\u5203\u7684\uFF0C\u6BD4\u8F83\u539A\u91CD\uFF0C\u7528\u6765\u780D\u65AD\u7816\u5757\u7684\uFF09\uFF0C\u8BA9\u4ED6\u4E13\u95E8\u780C\u63A9\u4F53\u7684\u5899\u3002</p>\n<p>\u6C49\u751F\u521A\u5F00\u59CB\u8FD8\u4E0D\u77E5\u9053\u600E\u4E48\u53BB\u7D66\u81EA\u5DF1\u7684\u513F\u5B50\u62A5\u4EC7\uFF0C\u62FF\u5230\u4E86\u6CE5\u5200\u4EE5\u540E\uFF0C\u4ED6\u77E5\u9053\u81EA\u5DF1\u8981\u505A\u4EC0\u4E48\u4E86\u3002</p>\n<p>\u4E8E\u662F\uFF0C\u5E72\u6D3B\u7684\u65F6\u5019\uFF0C\u4ED6\u90FD\u4F1A\u9694\u4E00\u6BB5\u5077\u5077\u62FF\u4E00\u5757\u7816\uFF0C\u80CC\u7740\u9B3C\u5B50\u5728\u7816\u9762\u4E0A\u7528\u6CE5\u5200\u523B\u4E24\u4E2A\u5C0F\u4EBA\uFF0C\u4E00\u7537\u4E00\u5973\uFF0C\u5C16\u7259\u5229\u9F7F\u7684\uFF0C\u7136\u540E\u54AC\u7834\u624B\u6307\uFF0C\u7528\u81EA\u5DF1\u7684\u8840\u62B9\u5728\u4E86\u5C0F\u4EBA\u8EAB\u4E0A\uFF0C\u5634\u91CC\u8FD8\u5FF5\u5FF5\u6709\u8BCD\uFF0C\u5F04\u597D\u4E86\u4EE5\u540E\uFF0C\u780C\u5230\u63A9\u4F53\u4E0A\u9762\uFF0C\u6BCF\u9694\u4E00\u6BB5\u4ED6\u5C31\u5F04\u4E00\u4E2A\u3002</p>\n<p>\u65C1\u8FB9\u51E0\u4E2A\u6CE5\u5320\u6162\u6162\u4E5F\u53D1\u73B0\u4E86\u4ED6\u8FD9\u6837\u505A\uFF0C\u90FD\u662F\u8FD9\u4E2A\u5730\u65B9\u4E0A\u7684\u540C\u884C\uFF0C\u5927\u5BB6\u90FD\u8BA4\u8BC6\uFF0C\u800C\u4E14\u8FD9\u6837\u7684\u4E8B\u60C5\uFF0C\u540C\u884C\u770B\u5230\u4E86\uFF0C\u4E5F\u4E0D\u4F1A\u7BA1\uFF01</p>\n<p>\u5982\u679C\u662F\u5728\u5E73\u65F6\uFF0C\u5E2E\u7740\u5176\u4ED6\u4EBA\u5BB6\u5E72\u6D3B\uFF0C\u770B\u5230\u4E86\u8FD9\u6837\u7684\uFF0C\u4ED6\u4EEC\u8FD8\u4F1A\u79C1\u4E0B\u91CC\u627E\u90A3\u4E2A\u6CE5\u5320\u8BF4\u4E00\u4E0B\uFF0C\u4E0D\u8981\u505A\u7684\u592A\u8FC7\u5206\uFF0C\u8BA9\u4EBA\u5BB6\u5BB6\u91CC\u7EDD\u4E86\uFF0C\u56E0\u4E3A\u6CE5\u5320\u80FD\u591F\u7834\u4E86\u540C\u884C\u7684\u5E03\u7F6E\uFF0C\u5230\u65F6\u5019\uFF0C\u5E03\u7F6E\u8FD9\u4E9B\u4E1C\u897F\u7684\u6CE5\u5320\uFF0C\u5C31\u8BE5\u5012\u9709\u4E86\uFF01\u800C\u4E14\u8981\u662F\u5EFA\u623F\u5B50\u7684\u4EBA\u5BB6\u77E5\u9053\u4E86\u6CE5\u5320\u505A\u7684\u8FD9\u4E2A\uFF0C\u4E5F\u4F1A\u53BB\u8BF7\u5176\u4ED6\u7684\u6CE5\u5320\u6765\u7834\uFF01</p>\n<p>\u4F46\u662F\u73B0\u5728\uFF0C\u4ED6\u4EEC\u662F\u5728\u7D66\u9B3C\u5B50\u5E72\u6D3B\uFF0C\u6240\u4EE5\u770B\u5230\u4E86\u4E5F\u5F53\u6CA1\u6709\u770B\u5230\u3002</p>\n<p>\u8FD9\u5929\u4F11\u606F\u7684\u65F6\u5019\uFF0C\u51E0\u4E2A\u6CE5\u5320\u5C31\u5750\u5230\u4E00\u5757\u4E86\u3002</p>\n<p>\u201C\u6C49\u751F\uFF0C\u4F60\u5E72\u561B\u554A\uFF1F\u5E03\u7F6E\u4E86\u90A3\u4E48\u591A\uFF1F\u4F60\u4E0D\u6015\u9B3C\u5B50\u627E\u4F60\u554A\uFF0C\u4E07\u4E00\u4ED6\u4EEC\u8981\u662F\u8BF7\u4EBA\u7834\u4E86\u4F60\u8FD9\u4E2A\u5492\uFF0C\u4F60\u8FD8\u8981\u4E0D\u8981\u6D3B\u4E86\uFF1F\u201D\u4E00\u4E2A\u8001\u5DE5\u5320\u5C0F\u58F0\u7684\u95EE\u7740\u6C49\u751F\u3002</p>\n<p>\u201C\u8FD8\u6D3B\u4EC0\u4E48\u554A\uFF1F\u6211\u4E09\u4E2A\u513F\u5B50\u90FD\u6B7B\u4E86\uFF0C\u8001\u5A46\u4E5F\u6B7B\u4E86\uFF0C\u4F60\u8BF4\u6211\u8FD8\u8981\u6D3B\u4EC0\u4E48\uFF1F\u201D\u6C49\u751F\u5750\u5728\u90A3\u91CC\uFF0C\u54AC\u7740\u7259\u8BF4\u9053\u3002</p>\n<p>\u201C\u4EC0\u4E48\uFF1F\u4E09\u4E2A\u513F\u5B50\uFF0C\u90FD\uFF0C\u90FD\u8D70\u4E86\uFF0C\u600E\u4E48\u56DE\u4E8B\uFF0C\u6CA1\u6709\u542C\u4F60\u8BF4\u8FC7\u554A\uFF1F\u201D\u90A3\u4E9B\u6CE5\u5320\u542C\u5230\u4E86\uFF0C\u90FD\u56F4\u4E86\u8FC7\u6765\u3002</p>\n<p>\u4ED6\u4EEC\u4E4B\u524D\u6839\u672C\u5C31\u4E0D\u77E5\u9053\u6C49\u751F\u5BB6\u7684\u4E8B\u60C5\uFF0C\u5C31\u77E5\u9053\u4ED6\u67092\u4E2A\u513F\u5B50\u8BFB\u4E66\u4E86\uFF0C\u5F53\u4E86\u519B\u5B98\uFF0C\u8FD8\u6709\u4E00\u4E2A\u5C0F\u513F\u5B50\uFF0C\u4E5F\u5728\u53BF\u57CE\u8BFB\u4E66\uFF01</p>\n<p>\u201C\u5927\u513F\u5B50\u6B7B\u5728\u4E0A\u6D77\uFF0C\u4E8C\u513F\u5B50\u6B7B\u5728\u5357\u4EAC\uFF0C\u5C0F\u513F\u5B50\uFF0C\u5C0F\u513F\u5B50\u88AB\u5C0F\u9B3C\u5B50\u7528\u523A\u5200\u6345\u4E86\uFF0C\u6B7B\u5728\u6211\u6000\u91CC\uFF01\u201D\u6C49\u751F\u8BF4\u7740\u5C31\u54ED\u4E86\u8D77\u6765\u3002</p>\n<p>\u90A3\u4E9B\u6CE5\u5320\u542C\u5230\u4E86\uFF0C\u5168\u90FD\u6C89\u9ED8\u4E86\uFF01</p>\n<p>\u201C\u54CE\uFF01\u4F60\u8BE5\u5E72\u5565\u5E72\u5565\uFF0C\u6211\u4EEC\u4F1A\u7D66\u5176\u4ED6\u7684\u540C\u884C\u8BF4\uFF0C\u8C01\u8981\u6765\u7834\uFF0C\u5C31\u4E0D\u8981\u602A\u6211\u4EEC\u4E0D\u5BA2\u6C14\u4E86\uFF01\u201D\u5176\u4E2D\u4E00\u4E2A\u8001\u6CE5\u5320\u8BF4\u9053\u3002</p>\n<p>\u201C\u6211\u4E0D\u6015\uFF0C\u6211\u8FD9\u4E2A\u662F\u6B7B\u5492\uFF01\u201D\u6C49\u751F\u62AC\u8D77\u5934\u6765\u8BF4\u9053\u3002</p>\n<p>\u201C\u4EC0\u4E48\uFF1F\u201D\u5176\u4ED6\u7684\u4EBA\u542C\u5230\u4E86\uFF0C\u66F4\u52A0\u9707\u60CA\uFF0C\u4E5F\u5C31\u662F\u8BF4\uFF0C\u6C49\u751F\u5728\u5E03\u7F6E\u5B8C\u4E86\u4EE5\u540E\uFF0C\u4ED6\u81EA\u5DF1\u4F1A\u81EA\u6740\uFF0C\u8C01\u6562\u7834\u5492\uFF0C\u90A3\u4E9B\u62A5\u5E94\u5C31\u4F1A\u843D\u5230\u8C01\u7684\u8EAB\u4E0A\uFF01</p>\n<p>\u63A5\u4E0B\u67651\u4E2A\u591A\u6708\uFF0C\u6C49\u751F\u753B\u4E86100\u591A\u5757\u8FD9\u6837\u7684\u7816\uFF0C\u6574\u4E2A\u65E5\u672C\u7684\u5730\u4E0B\u63A9\u4F53\uFF0C\u5168\u6709\u8FD9\u6837\u7684\u7816\u5757\u3002</p>\n<p>\u90A3\u4E9B\u767E\u59D3\u88AB\u9B3C\u5B50\u653E\u4E86\u4EE5\u540E\uFF0C\u6C49\u751F\u56DE\u5230\u4E86\u5BB6\u4E2D\uFF0C\u7D66\u81EA\u5DF1\u7684\u8001\u5A46\u5B69\u5B50\u70E7\u4E86\u5927\u91CF\u7684\u7EB8\u94B1\uFF0C\u4EBA\u5C31\u60AC\u5728\u4E86\u623F\u6881\u4E0A\u9762\uFF0C\u518D\u4E5F\u6CA1\u6709\u9192\u6765\u3002</p>\n<p>\u5F88\u5FEB\uFF0C\u65E5\u672C\u9B3C\u5B50\u5C31\u8FDB\u9A7B\u5230\u4E86\u5730\u4E0B\u63A9\u4F53\u5F53\u4E2D\uFF0C\u7B2C\u4E00\u5929\u665A\u4E0A\uFF0C\u9B3C\u5B50\u5728\u534A\u7761\u534A\u9192\u95F4\uFF0C\u603B\u80FD\u542C\u5230\u5C0F\u5B69\u5C16\u9510\u7684\u7B11\u58F0\uFF0C\u5F88\u591A\u9B3C\u5B50\u90FD\u88AB\u5413\u7684\u5750\u4E86\u8D77\u6765\uFF0C\u770B\u7740\u56DB\u5468\uFF0C\u4EC0\u4E48\u90FD\u6CA1\u6709\u3002</p>\n<p>\u4E00\u4E2A\u665A\u4E0A\uFF0C\u9B3C\u5B50\u4E0D\u77E5\u9053\u9192\u6765\u4E86\u591A\u5C11\u6B21\uFF0C\u4ED6\u4EEC\u4E5F\u627E\u8FC7\uFF0C\u5C31\u662F\u6CA1\u6709\u627E\u5230\u58F0\u97F3\u7684\u6765\u6E90\uFF0C\u665A\u4E0A\u6CA1\u6709\u7761\u597D\uFF0C\u767D\u5929\u5C31\u6253\u778C\u7761\uFF0C\u53EF\u662F\u6253\u778C\u7761\u8FD8\u662F\u80FD\u591F\u542C\u5230\u7B11\u58F0\u3002</p>\n<p>\u5230\u4E86\u7B2C\u4E8C\u5929\u665A\u4E0A\u7684\u65F6\u5019\uFF0C\u90A3\u4E9B\u9B3C\u5B50\u56F0\u7684\u4E0D\u884C\u4E86\uFF0C\u5012\u5730\u5C31\u7761\uFF0C\u7761\u5230\u534A\u591C\uFF0C\u8138\u523A\u75BC\u523A\u75BC\u7684\uFF0C\u611F\u89C9\u6709\u4EBA\u6320\u81EA\u5DF1\uFF0C\u6709\u7684\u9B3C\u5B50\u75BC\u7684\u9192\u6765\u4E86\uFF0C\u6478\u7740\u8138\uFF0C\u53D1\u73B0\u6E7F\u6E7F\u7684\uFF0C\u5C31\u5230\u6709\u706F\u5149\u7684\u5730\u65B9\u53BB\u770B\uFF0C\u53D1\u73B0\u5168\u662F\u8840\uFF0C\u4E0D\u77E5\u9053\u600E\u4E48\u56DE\u4E8B\u3002</p>\n<p>\u63A5\u7740\uFF0C\u8D8A\u6765\u8D8A\u591A\u7684\u9B3C\u5B50\u75BC\u9192\u4E86\uFF0C\u4ED6\u4EEC\u53D1\u73B0\u5F88\u591A\u4EBA\u7684\u8138\u90FD\u88AB\u6320\u70C2\u4E86\uFF0C\u6709\u7684\u8FD8\u662F\u88AB\u54AC\u70C2\u7684\uFF0C\u80FD\u591F\u6E05\u695A\u7684\u770B\u5230\u7259\u5370\uFF0C\u90A3\u4E9B\u9B3C\u5B50\u770B\u8D77\u6765\u975E\u5E38\u6050\u6016\uFF0C\u8840\u8089\u6A21\u7CCA\uFF0C\u53EA\u80FD\u627E\u6765\u533B\u751F\u6B62\u8840\uFF01</p>\n<p>\u9B3C\u5B50\u60F3\u8981\u77E5\u9053\u5230\u5E95\u662F\u600E\u4E48\u56DE\u4E8B\uFF0C\u662F\u4E0D\u662F\u6709\u4EC0\u4E48\u866B\u5B50\uFF0C\u4F46\u662F\u4ED6\u4EEC\u68C0\u67E5\u4E86\u5F88\u591A\u904D\uFF0C\u4EC0\u4E48\u90FD\u6CA1\u6709\u3002</p>\n<p>\u7B2C\u4E09\u5929\u665A\u4E0A\uFF0C\u9B3C\u5B50\u8FD8\u662F\u56F0\u7684\u4E0D\u884C\uFF0C\u7EE7\u7EED\u7761\u89C9\uFF0C\u8FD8\u6709\u4EBA\u4E13\u95E8\u5750\u5728\u90A3\u91CC\u503C\u73ED\uFF0C\u90A3\u4E9B\u503C\u73ED\u7684\u9B3C\u5B50\uFF0C\u62B1\u7740\u67AA\u8FF7\u8FF7\u7CCA\u7CCA\u7684\uFF0C\u5176\u4E2D\u4E00\u4E2A\u9B3C\u5B50\u611F\u89C9\u6709\u4EBA\u6320\u81EA\u5DF1\uFF0C\u5C31\u7528\u624B\u53BB\u62CD\uFF0C\u556A\u7684\u4E00\u58F0\u6253\u5728\u81EA\u5DF1\u7684\u8138\u4E0A\uFF0C\u4E0D\u4F46\u81EA\u5DF1\u7D66\u75BC\u6E05\u9192\u4E86\uFF0C\u8FD8\u628A\u5176\u4ED6\u7684\u4EBA\u7D66\u5413\u9192\u4E86\uFF0C\u90A3\u4E9B\u9B3C\u5B50\u770B\u5230\u6CA1\u4E8B\uFF0C\u5C31\u7EE7\u7EED\u7761\u7740\u3002</p>\n<p>\u7A81\u7136\u201C\u8F70\u201D\u7684\u4E00\u58F0\uFF0C\u4E00\u4E2A\u503C\u73ED\u6253\u778C\u7761\u7684\u9B3C\u5B50\u8170\u95F4\u7684\u624B\u96F7\u7206\u70B8\u4E86\uFF0C\u4E5F\u4E0D\u77E5\u9053\u662F\u600E\u4E48\u56DE\u4E8B\uFF0C\u8FD8\u70B8\u4F24\u4E86\u597D\u51E0\u4E2A\uFF0C\u6B7B\u4E862\u4E2A\uFF01</p>\n<p>\u9B3C\u5B50\u6709\u70B9\u6015\u4E86\uFF0C\u4E8E\u662F\u4E0D\u60F3\u5728\u63A9\u4F53\u91CC\u9762\u7761\uFF0C\u4F46\u662F\u6B66\u6C49\u4F1A\u6218\u90A3\u4E2A\u65F6\u5019\u5DF2\u7ECF\u6253\u54CD\u4E86\uFF0C\u8FD9\u8FB9\u662F\u91CD\u8981\u7684\u4E00\u9053\u9632\u7EBF\uFF0C\u6240\u4EE5\uFF0C\u4E0A\u9762\u7684\u547D\u4EE4\u662F\u5FC5\u987B\u5728\u8FD9\u91CC\u5B88\u7740\u3002</p>\n<p>\u63A5\u4E0B\u6765\uFF0C\u6BCF\u5929\u665A\u4E0A\u90FD\u6709\u6B7B\u4F24\u7684\u9B3C\u5B50\uFF0C\u6709\u7684\u9B3C\u5B50\u660E\u660E\u628A\u5B50\u5F39\u9000\u819B\u4E86\uFF0C\u67AA\u5C45\u7136\u8FD8\u54CD\u4E86\uFF0C\u8FD8\u80FD\u6253\u6B7B\u4EBA\u3002</p>\n<p>\u800C\u9B3C\u5B50\u7684\u624B\u96F7\u4E5F\u5F88\u5947\u602A\uFF0C\u672C\u6765\u9B3C\u5B50\u7684\u624B\u96F7\u662F\u8981\u62D4\u6389\u4FDD\u9669\u4EE5\u540E\uFF0C\u6572\u4E00\u4E0B\uFF0C\u6254\u51FA\u53BB\u624D\u4F1A\u7206\u70B8\u7684\uFF0C\u800C\u9B3C\u5B50\u7684\u624B\u96F7\uFF0C\u7ECF\u5E38\u65E0\u7F18\u65E0\u6545\u7684\u4ECE\u8170\u95F4\u6389\u4E0B\u6765\uFF0C\u4FDD\u9669\u8FD8\u677E\u4E86\uFF0C\u843D\u5728\u5730\u4E0A\u7684\u65F6\u5019\uFF0C\u9B3C\u5B50\u6B63\u5728\u8D70\u8DEF\u8E22\u4E86\u4E00\u4E0B\uFF0C\u5C45\u7136\u70B8\u4E86\uFF01</p>\n<p>\u6700\u8BA9\u9B3C\u5B50\u96BE\u53D7\u7684\u662F\uFF0C\u4ED6\u4EEC\u665A\u4E0A\u6839\u672C\u5C31\u4E0D\u8981\u60F3\u7761\u89C9\uFF0C\u53EA\u8981\u7761\u89C9\uFF0C\u80AF\u5B9A\u662F\u4F1A\u88AB\u75BC\u9192\u7684\uFF0C\u800C\u4E14\u8EAB\u4E0A\u5230\u5904\u90FD\u662F\u6307\u7532\u6320\u7684\u4F24\u53E3\uFF0C\u548C\u8840\u8FF9\u3002</p>\n<p>\u6574\u4E2A\u5730\u4E0B\u63A9\u4F53\u91CC\u9762\uFF0C\u6709\u9B3C\u5B50\u4E00\u4E2A\u5927\u961F\u7684\u58EB\u5175\uFF0C\u4ECE\u9B3C\u5B50\u5927\u961F\u957F\u5230\u4E0B\u9762\u7684\u58EB\u5175\uFF0C\u6BCF\u4E2A\u4EBA\u8EAB\u4E0A\u90FD\u662F\u5E26\u4F24\u7684\uFF0C\u800C\u4E14\u7CBE\u795E\u975E\u5E38\u7684\u840E\u9761\u3002</p>\n<p>\u9B3C\u5B50\u7684\u5927\u961F\u957F\u51E0\u6B21\u60F3\u8981\u8C03\u51FA\u8FD9\u4E2A\u5730\u65B9\uFF0C\u4F46\u662F\u4E0A\u9762\u7684\u547D\u4EE4\u575A\u6301\u8981\u4ED6\u4EEC\u575A\u5B88\u5728\u8FD9\u91CC\uFF0C\u903C\u7684\u9B3C\u5B50\u6CA1\u6709\u529E\u6CD5\uFF0C\u665A\u4E0A\u53EA\u80FD\u53BB\u63A9\u4F53\u5916\u9762\u7684\u7A7A\u5730\u4E0A\u7761\u89C9\u3002</p>\n<p>\u4F46\u662F\u767D\u5929\uFF0C\u4ED6\u4EEC\u4E0D\u6562\u5728\u5916\u9762\uFF0C\u90A3\u4E2A\u65F6\u5019\u6B63\u5728\u6253\u4ED7\uFF0C\u4ED6\u4EEC\u5FC5\u987B\u8981\u8FDB\u5165\u5230\u63A9\u4F53\u91CC\u9762\uFF0C\u6BCF\u5929\uFF0C\u63A9\u4F53\u91CC\u4E0D\u662F\u4F20\u6765\u7206\u70B8\u58F0\uFF0C\u5C31\u662F\u67AA\u58F0\uFF0C\u6BCF\u5929\u90FD\u8981\u4E00\u4E24\u4E2A\u5012\u9709\u7684\u9B3C\u5B50\u6B7B\u7684\u532A\u5937\u6240\u601D\u3002</p>\n<p>\u534A\u4E2A\u6708\u4EE5\u540E\uFF0C\u9B3C\u5B50\u77E5\u9053\u8FD9\u91CC\u80AF\u5B9A\u6709\u4E0D\u5E72\u51C0\u7684\u4E1C\u897F\uFF0C\u5C31\u53BB\u8BF7\u6765\u9053\u58EB\uFF0C\u5E0C\u671B\u4ED6\u80FD\u591F\u505A\u6CD5\uFF0C\u9053\u58EB\u6765\u4E86\uFF0C\u770B\u4E00\u4E0B\u8BF4\u4E0D\u884C\uFF0C\u8FD9\u4E2A\u4E1C\u897F\u4ED6\u7834\u4E0D\u4E86\uFF01</p>\n<p>\u4E0D\u8FC7\u4E5F\u544A\u8BC9\u4E86\u9B3C\u5B50\uFF0C\u8FD9\u4E2A\u662F\u6CE5\u5320\u5F04\u7684\uFF0C\u5FC5\u987B\u8981\u627E\u5230\u6CE5\u5320\u6765\u7834\u89E3\u624D\u884C\uFF0C\u800C\u4E14\u5FC5\u987B\u8981\u627E\u5230\u5F53\u65F6\u5E03\u7F6E\u7684\u90A3\u4E2A\u6CE5\u5320\u624D\u53EF\u4EE5\u3002</p>\n<p>\u4E8E\u662F\uFF0C\u9B3C\u5B50\u5C31\u53BB\u627E\u4E4B\u524D\u5728\u4FEE\u63A9\u4F53\u7684\u90A3\u4E9B\u6CE5\u5320\uFF0C\u4F46\u662F\u4E00\u4E2A\u90FD\u6CA1\u6709\u627E\u5230\uFF0C\u4E0D\u4F46\u4E4B\u524D\u7684\u90A3\u4E9B\u6CE5\u5320\u627E\u4E0D\u5230\uFF0C\u5C31\u662F\u9644\u8FD1\u51E0\u767E\u91CC\u7684\u6CE5\u5320\u90FD\u8DD1\u5149\u4E86\u3002</p>\n<p>\u4ED6\u4EEC\u90FD\u77E5\u9053\u6C49\u751F\u5E03\u7F6E\u4E86\u6B7B\u5492\uFF0C\u8C01\u53BB\u7834\u89E3\uFF0C\u62A5\u5E94\u5C31\u4F1A\u843D\u5728\u8C01\u7684\u8EAB\u4E0A\uFF0C\u52A0\u4E0A\u8FD9\u91CC\u6B63\u5728\u6253\u4ED7\uFF0C\u767E\u59D3\u4EEC\u4E5F\u9003\u96BE\u4E86\uFF0C\u6240\u4EE5\uFF0C\u9B3C\u5B50\u627E\u4E86\u51E0\u5929\uFF0C\u6CA1\u6709\u6293\u5230\u4E00\u4E2A\u6CE5\u5320\u56DE\u6765\uFF0C\u53EA\u80FD\u7EE7\u7EED\u71AC\u7740\u3002</p>\n<p>\u51E0\u5929\u4EE5\u540E\uFF0C\u4E2D\u56FD\u7684\u90E8\u961F\u53D1\u73B0\u4E86\u9B3C\u5B50\u665A\u4E0A\u90FD\u662F\u5728\u63A9\u4F53\u5916\u9762\u7761\u89C9\uFF0C\u5C31\u6478\u4E86\u8FC7\u53BB\uFF0C\u5F53\u5929\u665A\u4E0A\uFF0C\u4E2D\u56FD\u7684\u90E8\u961F\u5C31\u628A\u8FD9\u4E2A\u63A9\u4F53\u91CC\u9762\u7684\u9B3C\u5B50\u7D66\u5168\u6B7C\u4E86\u3002</p>\n';
	var text_candy = '\n<p>\u591C\u8272\u4E0B\uFF0C\u5DEB\u65CF\u7981\u5730\uFF0C\u9634\u68EE\u745F\u745F\uFF0C\u6BEB\u65E0\u4EBA\u6C14\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u7F13\u6B65\u8D70\u5728\u7981\u5730\u4E4B\u4E2D\u7684\u83B2\u82B1\u697C\u4E0A\uFF0C\u8FD9\u5EA7\u697C\u5E74\u4E45\u5931\u4FEE\uFF0C\u6BCF\u8FC8\u52A8\u4E00\u6B65\uFF0C\u90FD\u4F1A\u53D1\u51FA\u5431\u5440\u5431\u5440\u7684\u58F0\u97F3\uFF0C\u5728\u5BC2\u9759\u7684\u591C\u8272\u4E0B\uFF0C\u542C\u5728\u666E\u901A\u4EBA\u8033\u4E2D\uFF0C\u683C\u5916\u7684\u7606\u4EBA\u3002</p>\n<p>\u56E0\u4E3A\u5979\u662F\u9634\u6708\u9634\u65E5\u751F\u7684\u5973\u5B50\uFF0C\u5DF2\u5E74\u6EE1\u5341\u516D\uFF0C\u6240\u4EE5\u88AB\u5BB6\u65CF\u9009\u6765\u4F8D\u5949\u201C\u9B3C\u5C11\u201D\u3002</p>\n<p>\u5979\u6CA1\u5F97\u9009\u62E9\uFF0C\u6709\u65F6\u5019\u4EBA\u5FC3\u6BD4\u9B3C\u66F4\u52A0\u7684\u53EF\u6016\u3002</p>\n<p>\u4E5F\u4E0D\u77E5\u9053\u8D70\u4E86\u591A\u4E45\uFF0C\u8FD9\u5EA7\u83B2\u82B1\u697C\u5F88\u9AD8\u5F88\u9AD8\uFF0C\u4EFF\u4F5B\u901A\u5929\u800C\u4E0A\u3002</p>\n<p>\u6709\u4F20\u8A00\u8BF4\uFF0C\u53EA\u6709\u5728\u6708\u5706\u4E4B\u591C\uFF0C\u8FD9\u5EA7\u83B2\u82B1\u697C\u624D\u4F1A\u51FA\u73B0\uFF0C\u542C\u8BF4\u6CA1\u6709\u6D3B\u4EBA\u6562\u8E0F\u5165\u8FD9\u6837\u7684\u5730\u65B9\uFF0C\u56E0\u4E3A\u8FD9\u91CC\u662F\u5DEB\u65CF\u5C11\u7237\u5DEB\u5343\u9B45\u751F\u6D3B\u7684\u5730\u65B9\uFF0C\u4ED6\u4E0D\u559C\u88AB\u6253\u6270\u3002</p>\n<p>\u53EF\u662F\u8C01\u90FD\u77E5\u9053\uFF0C\u90A3\u4F4D\u5DEB\u65CF\u60CA\u624D\u7EDD\u8273\u7684\u5C11\u7237\uFF0C\u5DF2\u7ECF\u6B7B\u53BB\u4E86\u767E\u5E74\u3002</p>\n<p>\u5173\u4E8E\u8FD9\u4F4D\u5C11\u7237\uFF0C\u6C11\u95F4\u6709\u592A\u591A\u592A\u591A\u7684\u4F20\u8BF4\u4E86\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u54AC\u4E86\u54AC\u5507\u74E3\uFF0C\u5B9A\u4E86\u5B9A\u5FC3\u795E\uFF0C\u7EE7\u7EED\u5F80\u4E0A\u8D70\uFF0C\u56E0\u4E3A\u5979\u80FD\u611F\u89C9\u5230\uFF0C\u82E5\u662F\u5979\u505C\u4E0B\u6765\uFF0C\u5C31\u4F1A\u88AB\u8FD9\u80A1\u9634\u98CE\u51BB\u50F5\u3002</p>\n<p>\u7EC8\u4E8E\u8E0F\u5165\u4E86\u83B2\u82B1\u697C\u4E2D\uFF0C\u7EEF\u6E05\u96C5\u521A\u5598\u53E3\u6C14\uFF0C\u5C31\u542C\u5230\u697C\u4E0A\u7684\u94C3\u94DB\u4E0D\u65AD\u54CD\u52A8\uFF0C\u867D\u7136\u58F0\u97F3\u5F88\u6E05\u8106\uFF0C\u4F46\u8FD8\u662F\u7A81\u7136\u5413\u4E86\u7EEF\u6E05\u96C5\u4E00\u8DF3\u3002</p>\n<p>\u201C\u54AF\u54AF\uFF0C\u6765\u6D3B\u4EBA\u4E86\u2026\u2026\u201D</p>\n<p>\u201C\u53C8\u4E00\u4E2A\u9001\u6B7B\u7684\u2026\u2026\u201D</p>\n<p>\u201C\u4EBA\u5FC3\u5440\uFF0C\u8D2A\u5A6A\u6210\u6027\u2026\u2026\u201D</p>\n<p>\u201C\u7528\u4E0D\u7528\u901A\u77E5\u5C11\u7237\uFF1F\u6216\u8BB8\u4F1A\u662F\u5C11\u7237\u7B49\u7684\u4EBA\u4E5F\u8BF4\u4E0D\u5B9A\u3002\u201D</p>\n<p>\u2026\u2026</p>\n<p>\u9634\u98CE\u745F\u745F\uFF0C\u9634\u51B7\u7684\u58F0\u97F3\u54CD\u5F7B\u5728\u8033\u8FB9\uFF0C\u7EEF\u6E05\u96C5\u5FCD\u4E0D\u4F4F\u5168\u8EAB\u53D1\u6296\uFF0C\u4E0A\u4E0B\u7259\u9F7F\u90FD\u5728\u6253\u98A4\uFF0C\u771F\u7684\u6709\u9B3C\uFF1F</p>\n<p>\u5979\u4E24\u53EA\u624B\u63E1\u6210\u62F3\u5934\uFF0C\u52AA\u529B\u8BA9\u81EA\u5DF1\u4E0D\u6655\u5012\u3002</p>\n<p>\u8FD9\u65F6\u5019\uFF0C\u5979\u7741\u5F00\u773C\u775B\u770B\u7740\u524D\u65B9\uFF0C\u53D1\u73B0\u90A3\u91CC\u6302\u7740\u4E00\u526F\u53E4\u753B\uFF0C\u867D\u7136\u53EA\u662F\u4E00\u773C\uFF0C\u4F46\u5979\u8FD8\u662F\u88AB\u753B\u7ED9\u5438\u5F15\u4F4F\u4E86\u5FC3\u795E\uFF0C\u90FD\u5FD8\u8BB0\u4E86\u5BB3\u6015\u3002</p>\n<p>\u753B\u4E0A\u753B\u7684\u662F\u4E00\u4F4D\u7537\u5B50\uFF0C\u9526\u8863\u7389\u534E\uFF0C\u7CBE\u81F4\u7EDD\u4F26\uFF0C\u51FA\u5C18\u8131\u4FD7\uFF0C\u4ED6\u62FF\u7740\u7389\u7B1B\u4F18\u96C5\u7684\u7AD9\u7740\uFF0C\u5634\u89D2\u6302\u7740\u6D45\u6D45\u7684\u7B11\u610F\uFF0C\u8BA9\u4EBA\u770B\u4E86\u5982\u6C90\u6625\u98CE\uFF0C\u4ED6\u6574\u4E2A\u4EBA\u4EFF\u4F5B\u655B\u5C3D\u5929\u5730\u7684\u98CE\u534E\u2026\u2026</p>\n<p>\u7EEF\u6E05\u96C5\u4E00\u65F6\u95F4\u518D\u627E\u4E0D\u5230\u8BCD\u8BED\u6765\u5F62\u5BB9\u753B\u4E2D\u7684\u4EBA\u3002</p>\n<p>\u5979\u60F3\u8D77\u4F20\u8A00\u8BF4\uFF0C\u83B2\u82B1\u697C\u4E2D\u7684\u9B3C\u5C11\uFF0C\u4E5F\u5C31\u662F\u5DEB\u5343\u9B45\uFF0C\u4ED6\u5728\u767E\u5E74\u524D\uFF0C\u4E5F\u662F\u98CE\u534E\u7EDD\u4EE3\u7684\u4EBA\u7269\uFF0C\u96BE\u9053\u8FD9\u5E45\u753B\u4E2D\u7684\u4EBA\u662F\u4ED6\uFF1F</p>\n<p>\u201C\u597D\u770B\u5417\uFF1F\u201D</p>\n<p>\u5728\u7EEF\u6E05\u96C5\u51FA\u795E\u7684\u65F6\u5019\uFF0C\u7A81\u7136\u8033\u8FB9\u54CD\u8D77\u4E00\u4E2A\u5E7D\u8C37\u7A7A\u7075\u7684\u58F0\u97F3\uFF0C\u5979\u8C41\u7136\u8F6C\u8EAB\u770B\u53BB\uFF0C\u60CA\u7684\u8FDE\u8FDE\u540E\u9000\uFF0C\u4EC0\u4E48\u65F6\u5019\u753B\u91CC\u7684\u4EBA\u7ADF\u7136\u51FA\u73B0\u5728\u8EAB\u8FB9\uFF0C\u5979\u4E3A\u4EC0\u4E48\u90FD\u6CA1\u5BDF\u89C9\uFF1F</p>\n<p>\u201C\u522B\u6015\uFF01\u201D\u7537\u5B50\u6E05\u6DA6\u7684\u58F0\u97F3\u5E26\u7740\u4E00\u4E1D\u7684\u51C4\u6E05\uFF0C\u5374\u4F3C\u5B89\u629A\u7EEF\u6E05\u96C5\u7684\u60C5\u7EEA\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u77AA\u5927\u773C\u775B\u770B\u7740\u773C\u524D\u5982\u6708\u534E\u5E7B\u5F71\u822C\u7684\u5F71\u50CF\uFF0C\u5979\u54C6\u55E6\u7740\u5507\u74E3\uFF0C\u95EE\u9053\uFF1A\u201C\u4F60\u2026\u2026\u662F\u4EBA\uFF0C\u662F\u9B3C\uFF1F\u201D</p>\n<p>\u7537\u5B50\u6DF1\u6DF1\u7684\u770B\u7740\u7EEF\u6E05\u96C5\uFF0C\u773C\u5E95\u7684\u4F24\u75DB\u90A3\u4E48\u7684\u660E\u663E\uFF0C\u4F3C\u6709\u54C0\u6028\uFF0C\u75F4\u7F20\uFF0C\u75DB\u82E6\u2026\u2026</p>\n<p>\u7EEF\u6E05\u96C5\u770B\u7740\u8FD9\u6837\u7684\u76EE\u5149\uFF0C\u4E0D\u77E5\u4E3A\u4F55\uFF0C\u5FC3\u5E95\u7ADF\u7136\u4E5F\u8DDF\u7740\u75BC\u4E86\u8D77\u6765\u3002</p>\n<p>\u201C\u5982\u679C\u6211\u662F\u9B3C\uFF0C\u4F60\u4F1A\u6015\u5417\uFF1F\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u5DF2\u7ECF\u540E\u9000\u5230\u4E86\u684C\u5B50\u524D\uFF0C\u5979\u5C06\u624B\u6309\u5728\u684C\u5B50\u4E0A\uFF0C\u52AA\u529B\u6491\u4F4F\u81EA\u5DF1\uFF0C\u4E0D\u8BA9\u81EA\u5DF1\u5012\u4E0B\u3002</p>\n<p>\u201C\u4F60\u2026\u2026\u4F60\u4F1A\u5403\u4E86\u6211\u5417\uFF1F\u201D</p>\n<p>\u542C\u5230\u8FD9\u53E5\u8BDD\uFF0C\u7537\u5B50\u7A81\u7136\u8F7B\u8F7B\u7B11\u4E86\u8D77\u6765\uFF0C\u4ED6\u7B11\u8D77\u6765\u7279\u522B\u7684\u597D\u770B\uFF0C\u829D\u5170\u7389\u6811\uFF0C\u6CE3\u8840\u5982\u534E\uFF0C\u98CE\u534E\u65E0\u53CC\u2026\u2026</p>\n<p>\u800C\u4E14\u58F0\u97F3\u4EFF\u4F5B\u60A0\u8FDC\u7684\u7434\u97F3\uFF0C\u90A3\u6837\u7684\u60A6\u8033\u4F4E\u6C89\uFF0C\u8BA9\u4EBA\u5FCD\u4E0D\u4F4F\u6C89\u9189\u5176\u4E2D\u3002</p>\n<p>\u987B\u81FE\uFF0C\u7537\u5B50\u6B62\u4F4F\u4E86\u7B11\u5BB9\uFF0C\u4ED6\u7CBE\u81F4\u7684\u7709\u773C\u6DF1\u6DF1\u7684\u9501\u4F4F\u7EEF\u6E05\u96C5\u7684\u7738\u5149\uFF0C\u9053\uFF1A\u201C\u5982\u679C\u6211\u8BF4\u4F1A\u5403\u4E86\u4F60\u5462\uFF1F\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u4E0D\u65AD\u7684\u6447\u5934\uFF0C\u201C\u4F60\u522B\u5403\u6211\uFF0C\u6211\u4E0D\u597D\u5403\u7684\u2026\u2026\u6211\u53EF\u4EE5\u505A\u5F88\u591A\u4E8B\u60C5\uFF0C\u4E5F\u53EF\u4EE5\u966A\u4F60\u89E3\u95F7\uFF0C\u6211\u4F1A\u8BB2\u6545\u4E8B\u2026\u2026\u201D</p>\n<p>\u5979\u7EDE\u5C3D\u8111\u6C41\u7684\u60F3\u7406\u7531\uFF0C\u4F46\u662F\u5E73\u65E5\u5C31\u4E0D\u592A\u7231\u8BF4\u7684\u4EBA\uFF0C\u5230\u4E86\u8FD9\u4E2A\u65F6\u5019\uFF0C\u4E5F\u5B9E\u5728\u60F3\u4E0D\u51FA\u592A\u5177\u6709\u8BF4\u670D\u529B\u7684\u7406\u7531\u3002</p>\n<p>\u7A81\u7136\u5979\u773C\u7738\u5927\u4EAE\uFF0C\u201C\u4F60\u2026\u2026\u4F60\u662F\u5DEB\u5343\u9B45\u662F\u5417\uFF1F\u90FD\u8BF4\u4F60\u751F\u524D\u6700\u597D\u4E86\uFF0C\u4F60\u5C31\u7B97\u662F\u4E2A\u9B3C\uFF0C\u4E5F\u662F\u4E2A\u597D\u9B3C\uFF0C\u4F60\u653E\u4E86\u6211\uFF0C\u597D\u4E0D\u597D\uFF1F\u53EA\u8981\u522B\u5403\u6211\u5C31\u884C\u3002\u201D</p>\n<p>\u7537\u5B50\u7A7A\u7075\u5E7D\u53F9\u4E00\u58F0\uFF0C\u5728\u5BC2\u9759\u7684\u697C\u5B87\u4E0A\uFF0C\u591A\u4E86\u82CD\u51C9\u7684\u610F\u5473\u3002</p>\n<p>\u201C\u50BB\u74DC\uFF0C\u6211\u8BF4\u7684\u5403\uFF0C\u4E0D\u662F\u4F60\u4EE5\u4E3A\u7684\u5403\u3002\u201D</p>\n<p>\u542C\u5230\u8FD9\u53E5\u8BDD\uFF0C\u7EEF\u6E05\u96C5\u63D0\u7740\u7684\u5FC3\u624D\u843D\u4E0B\uFF0C\u8FD9\u4E00\u4F1A\u529F\u592B\uFF0C\u5979\u5DF2\u7ECF\u60CA\u60E7\u7684\u5168\u8EAB\u51FA\u51B7\u6C57\u4E86\u3002</p>\n<p>\u201C\u5475\uFF0C\u751F\u524D\u597D\u5417\uFF1F\u53EF\u662F\u6211\u5374\u4F24\u5BB3\u4E86\u6211\u7684\u59BB\u5B50\u3002\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u6CA1\u63D2\u8BDD\uFF0C\u5979\u611F\u89C9\u5230\u773C\u524D\u8FD9\u4E2A\u9B3C\u5FC3\u60C5\u4E0D\u597D\uFF0C\u5979\u8FD8\u662F\u5C11\u60F9\u4ED6\u4E3A\u597D\u3002</p>\n<p>\u5979\u662F\u542C\u8BF4\u8FC7\u7684\uFF0C\u5DEB\u5343\u9B45\u7684\u59BB\u5B50\u6B7B\u540E\uFF0C\u4ED6\u6027\u60C5\u5927\u53D8\uFF0C\u6740\u4E86\u5DEB\u65CF\u7684\u5F88\u591A\u4EBA\uFF0C\u90A3\u4E00\u573A\u53D8\u6545\u540E\uFF0C\u5DEB\u65CF\u5728\u5357\u6052\u56FD\u7684\u5730\u4F4D\u4E00\u843D\u5343\u4E08\u3002</p>\n<p>\u770B\u7740\u5DEB\u5343\u9B45\u8427\u745F\u865A\u5E7B\u7684\u8EAB\u5F71\uFF0C\u7EEF\u6E05\u96C5\u8BF4\u4E0D\u6E05\u5FC3\u91CC\u662F\u4EC0\u4E48\u611F\u53D7\uFF0C\u5979\u5C31\u89C9\u5F97\u81EA\u5DF1\u5FC3\u91CC\u5F88\u75BC\u5F88\u95F7\uFF0C\u6709\u4E9B\u547C\u5438\u4E0D\u4E0A\u6765\u3002</p>\n<p>\u5979\u5FCD\u4E0D\u4F4F\u5F00\u53E3\u9053\uFF1A\u201C\u4F60\u2026\u2026\u4F60\u751F\u524D\uFF0C\u4E0D\u7231\u4F60\u7684\u59BB\u5B50\uFF0C\u4E3A\u4EC0\u4E48\u8981\u5A36\u5979\uFF1F\u201D</p>\n<p>\u7537\u5B50\u7A81\u7136\u795E\u8272\u4E00\u5389\uFF0C\u9053\uFF1A\u201C\u8C01\u8BF4\u6211\u4E0D\u7231\u5979\uFF1F\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u88AB\u8FD9\u7A81\u7136\u653E\u5927\u7684\u58F0\u97F3\u5413\u4E86\u4E00\u8DF3\uFF0C\u518D\u4E0D\u6562\u8BF4\u8BDD\u3002</p>\n<p>\u5DEB\u5343\u9B45\u865A\u5E7B\u7684\u8EAB\u5F71\u7F13\u7F13\u9760\u8FD1\u7EEF\u6E05\u96C5\uFF0C\u6E29\u58F0\u9053\uFF1A\u201C\u6E05\u6E05\uFF0C\u522B\u6015\u6211\u597D\u5417\uFF1F\u201D</p>\n<p>\u542C\u7740\u8FD9\u53E5\u8BDD\uFF0C\u7EEF\u6E05\u96C5\u672C\u6765\u53D1\u6296\u7684\u8EAB\u4F53\u7A81\u7136\u95F4\u6162\u6162\u5E73\u590D\u4E0B\u6765\uFF0C\u4E5F\u8BB8\u5979\u771F\u7684\u4E0D\u6015\u4E86\u3002</p>\n<p>\u5DEB\u5343\u9B45\u7F13\u7F13\u4F38\u624B\u62B1\u4F4F\u7EEF\u6E05\u96C5\uFF0C\u201C\u6211\u7EC8\u4E8E\u627E\u5230\u4F60\u4E86\uFF0C\u6211\u7B49\u4E86\u4F60\u767E\u5E74\u3002\u201D</p>\n<p>\u201C\u4E3A\u4EC0\u4E48\u6211\u7684\u5FC3\u597D\u75BC\uFF0C\u597D\u75BC\u2026\u2026\u201D\u7EEF\u6E05\u96C5\u7A81\u7136\u8138\u8272\u53D1\u767D\uFF0C\u6342\u7740\u5FC3\u53E3\uFF0C\u7F13\u7F13\u8E72\u4E0B\u3002</p>\n<p>\u201C\u6E05\u6E05\uFF0C\u8BB0\u4F4F\uFF0C\u4F60\u662F\u6211\u7684\u59BB\u5B50\uFF0C\u6211\u7231\u4F60\u3002\u201D</p>\n<p>\u201C\u6211\u4E0D\u662F\uFF0C\u6211\u4E0D\u662F\uFF0C\u597D\u75BC\u2026\u2026\u201D\u7EEF\u6E05\u96C5\u611F\u89C9\u8111\u6D77\u91CC\u4F3C\u4E4E\u6709\u4EC0\u4E48\u5F71\u50CF\u4E00\u4E0B\u5B50\u6D8C\u5165\u5979\u7684\u8111\u6D77\u91CC\u3002</p>\n<p>\u597D\u50CF\u662F\u5979\u548C\u773C\u524D\u7537\u5B50\u751F\u6D3B\u7684\u5F71\u50CF\u3002</p>\n<p>\u4ED6\u4EEC\u96BE\u9053\u771F\u7684\u8BA4\u8BC6\uFF1F\u5979\u662F\u4ED6\u7684\u59BB\u5B50\u5417\uFF1F</p>\n<p>\u6708\u534E\u4E2D\u4E0A\uFF0C\u5DEB\u5343\u9B45\u8EAB\u4F53\u7684\u5E7B\u5F71\u8D8A\u6765\u8D8A\u98D8\u6E3A\uFF0C\u4EFF\u4F5B\u8981\u98D8\u6563\u5F00\uFF0C\u4ED6\u8F7B\u58F0\u9053\uFF1A\u201C\u6E05\u6E05\uFF0C\u4F60\u8DDF\u6211\u6765\uFF0C\u8DDF\u6211\u6765\u5C31\u4E0D\u75BC\u4E86\u2026\u2026\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u604D\u60DA\u7684\u8DDF\u7740\u7537\u5B50\u7684\u5E7B\u5F71\u5F80\u524D\u8D70\uFF0C\u6574\u4E2A\u4EBA\u8D70\u51FA\u4E86\u83B2\u82B1\u697C\uFF0C\u77AC\u95F4\u8E29\u7A7A\uFF0C\u201C\u554A\u201D\u7684\u4E00\u58F0\uFF0C\u4ECE\u83B2\u82B1\u697C\u4E0A\u6389\u843D\u4E0B\u2026\u2026</p>\n<p>\u2026\u2026</p>\n<p>\u5DEB\u65CF\uFF0C\u6E05\u6708\u5C45</p>\n<p>\u201C\u554A\u2026\u2026\u201D\u7684\u4E00\u58F0\uFF0C\u7EEF\u6E05\u96C5\u4E00\u4E0B\u5B50\u9192\u4E86\u8FC7\u6765\uFF0C\u60CA\u5750\u8D77\uFF0C\u7136\u540E\u5927\u53E3\u5927\u53E3\u7684\u5598\u606F\u3002</p>\n<p>\u201C\u592B\u4EBA\uFF0C\u600E\u4E48\u4E86\uFF1F\u505A\u68A6\u4E86\u5417\uFF1F\u201D\u4E00\u4E2A\u6E29\u67D4\u7684\u58F0\u97F3\u5728\u8033\u8FB9\u54CD\u8D77\uFF0C\u4E00\u53CC\u4FEE\u957F\u5982\u7389\u7684\u624B\u8F7B\u8F7B\u7684\u7ED9\u5979\u64E6\u62ED\u989D\u5934\u4E0A\u7684\u6C57\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u4F4E\u5934\u4E00\u770B\uFF0C\u5927\u7EA2\u9E33\u9E2F\u9526\u88AB\uFF0C\u81EA\u5DF1\u7ADF\u7136\u4E0D\u7740\u5BF8\u7F15\uFF0C\u5979\u504F\u5934\u770B\u7740\u8EAB\u8FB9\u7684\u4EBA\uFF0C\u58A8\u53D1\u6563\u843D\uFF0C\u7709\u773C\u5982\u753B\uFF0C\u662F\u5DEB\u5343\u9B45\u6CA1\u9519\u3002</p>\n<p>\u201C\u4F60\u662F\u4EBA\u662F\u9B3C\uFF1F\u201D</p>\n<p>\u5DEB\u5343\u9B45\u795E\u8272\u5FAE\u53D8\uFF0C\u7136\u540E\u63FD\u8FC7\u7EEF\u6E05\u96C5\uFF0C\u4F4E\u5934\u543B\u4E86\u543B\u5979\u7684\u5507\u74E3\uFF0C\u201C\u592B\u4EBA\u8BF4\u80E1\u8BDD\u4E86\uFF0C\u4ECA\u591C\u662F\u6211\u4EEC\u7684\u6D1E\u623F\u82B1\u70DB\u591C\u3002\u201D</p>\n<p>\u5728\u5DEB\u5343\u9B45\u7684\u5B89\u629A\u4E0B\uFF0C\u7EEF\u6E05\u96C5\u7F13\u7F13\u56DE\u795E\uFF0C\u5979\u6478\u7740\u5DEB\u5343\u9B45\u7684\u624B\u81C2\uFF0C\u6709\u6E29\u5EA6\u7684\uFF0C\u4ED6\u662F\u4EBA\uFF01</p>\n<p>\u90A3\u5979\u53C8\u662F\u8C01\uFF1F</p>\n<p>\u5DEB\u5343\u9B45\u62B1\u7740\u7EEF\u6E05\u96C5\uFF0C\u8010\u5FC3\u7684\u9053\uFF1A\u201C\u4F60\u5FD8\u4E86\u5417\uFF1F\u4F60\u662F\u7EEF\u5BB6\u7684\u5C0F\u59D0\uFF0C\u6211\u4EEC\u81EA\u5E7C\u5B9A\u4EB2\uFF0C\u5982\u4ECA\u5DF2\u7ECF\u6210\u4EB2\uFF0C\u800C\u4E14\u6628\u591C\u6211\u4EEC\u4E5F\u5DF2\u5706\u623F\u3002\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u5C06\u4E4B\u524D\u7684\u4E8B\u60C5\u4E5F\u60F3\u8D77\u6765\u4E86\uFF0C\u95EE\u9053\uFF1A\u201C\u4F60\u4E0D\u662F\u4E0D\u6EE1\u610F\u8FD9\u95E8\u4EB2\u4E8B\u5417\uFF1F\u201D</p>\n<p>\u5DEB\u5343\u9B45\u53F9\u4E86\u53E3\u6C14\u9053\uFF1A\u201C\u8FD9\u662F\u6211\u81EA\u5DF1\u6C42\u6765\u7684\uFF0C\u600E\u4F1A\u4E0D\u6EE1\u610F\uFF0C\u4ECE\u8FDB\u5F80\u540E\u4F60\u662F\u6211\u7684\u59BB\u5B50\uFF0C\u6211\u4F1A\u7231\u4F60\u62A4\u4F60\uFF0C\u4E0D\u8BA9\u4F60\u53D7\u4EFB\u4F55\u59D4\u5C48\u3002\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u6014\u6014\u7684\u770B\u4E86\u5DEB\u5343\u9B45\u534A\u664C\uFF0C\u4ED6\u8BF4\u7684\u50CF\u662F\u771F\u7684\u4E00\u6837\u3002</p>\n<p>\u53EF\u662F\u5979\u7684\u5934\u7279\u522B\u7684\u6DF7\u4E71\uFF0C\u592A\u591A\u592A\u591A\u7684\u5F71\u50CF\u6D8C\u5165\u8111\u6D77\u91CC\uFF0C\u5979\u5206\u4E0D\u6E05\u68A6\u5883\u8FD8\u662F\u73B0\u5B9E\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u4F7F\u52B2\u7684\u6376\u6253\u81EA\u5DF1\u7684\u5934\u3002</p>\n<p>\u5DEB\u5343\u9B45\u5FC3\u75BC\u9053\uFF1A\u201C\u522B\u4F24\u5BB3\u81EA\u5DF1\u3002\u201D</p>\n<p>\u8FDE\u7EED\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\uFF0C\u7EEF\u6E05\u96C5\u90FD\u5206\u4E0D\u6E05\u68A6\u5883\u548C\u73B0\u5B9E\uFF0C\u4F46\u662F\u5DEB\u5343\u9B45\u5BF9\u5979\u662F\u771F\u7684\u5F88\u597D\u5F88\u597D\uFF0C\u5916\u9762\u7684\u4EBA\u90FD\u7FA1\u6155\u5979\u3002</p>\n<p>\u53EF\u5979\u5C31\u662F\u89C9\u5F97\u4E0D\u771F\u5B9E\uFF0C\u4E4B\u524D\u5979\u7236\u4EB2\u8FD8\u8BF4\uFF0C\u5DEB\u65CF\u7684\u5C11\u7237\u4E0D\u6EE1\u610F\u8FD9\u95E8\u4EB2\u4E8B\uFF0C\u8FD8\u60F3\u9000\u5A5A\u3002</p>\n<p>\u540E\u6765\u53C8\u662F\u4EC0\u4E48\u8BA9\u5DEB\u5343\u9B45\u6539\u53D8\u4E3B\u610F\u5462\uFF1F</p>\n<p>\u5BF9\u7EEF\u6E05\u96C5\u6765\u8BF4\uFF0C\u8FD9\u4E2D\u95F4\u6709\u592A\u591A\u592A\u591A\u7684\u8C1C\u56E2\u3002</p>\n<p>\u5979\u89C9\u5F97\u8D8A\u4E0D\u771F\u5B9E\uFF0C\u5DEB\u5343\u9B45\u5BF9\u5979\u5C31\u8D8A\u662F\u597D\uFF0C\u5979\u559C\u6B22\u7684\u4E1C\u897F\uFF0C\u4ED6\u90FD\u4F1A\u5F04\u6765\u7ED9\u5979\u73A9\uFF0C\u5979\u60F3\u5403\u7684\uFF0C\u518D\u8FDC\uFF0C\u4ED6\u4E5F\u4F1A\u8BA9\u4EBA\u53BB\u5F04\u6765\u7ED9\u5979\u5403\u3002</p>\n<p>\u800C\u4E14\u4ED6\u8FD8\u662F\u6574\u4E2A\u56FD\u5EA6\u516C\u8BA4\u7684\u5927\u5584\u4EBA\uFF0C\u4ED6\u6BCF\u5E74\u90FD\u4F1A\u6350\u8D60\u5F88\u591A\u7269\u8D44\u7ED9\u767E\u59D3\uFF0C\u8FD8\u4F1A\u62FF\u94B1\u4FEE\u8DEF\uFF0C\u6709\u4EBA\u9700\u8981\u5E2E\u52A9\uFF0C\u4ED6\u90FD\u5C3D\u91CF\u4F38\u51FA\u63F4\u52A9\u4E4B\u624B\u3002</p>\n<p>\u4ED6\u592A\u8FC7\u7F8E\u597D\uFF0C\u81EA\u7136\u6709\u4EBA\u5C06\u4E3B\u610F\u6253\u5230\u4ED6\u8EAB\u4E0A\uFF0C\u90FD\u60F3\u9001\u5973\u4EBA\u7ED9\u4ED6\uFF0C\u53EF\u662F\u5DEB\u5343\u9B45\u5168\u90E8\u90FD\u62D2\u7EDD\u4E86\u3002</p>\n<p>\u6709\u4E00\u5929\u591C\u91CC\uFF0C\u6069\u7231\u540E\uFF0C\u7EEF\u6E05\u96C5\u5FCD\u4E0D\u4F4F\u95EE\u4ED6\uFF0C\u201C\u4F60\u4E3A\u4EC0\u4E48\u72EC\u72EC\u5BF9\u6211\u597D\uFF1F\u6BD4\u6211\u6F02\u4EAE\u806A\u660E\u7684\u5973\u4EBA\u6709\u5F88\u591A\u3002\u201D</p>\n<p>\u201C\u53EF\u662F\u4ED6\u4EEC\u90FD\u4E0D\u662F\u6E05\u6E05\u4F60\u3002\u201D</p>\n<p>\u65E5\u5B50\u5C31\u8FD9\u6837\u6309\u90E8\u5C31\u73ED\u7684\u8FC7\u7740\uFF0C\u76F4\u5230\u6709\u4E00\u5929\uFF0C\u7EEF\u6E05\u96C5\u9047\u5230\u4E86\u4E00\u4F4D\u9053\u58EB\u3002</p>\n<p>\u90A3\u4F4D\u9053\u58EB\u4F3C\u4E4E\u8981\u8D76\u8DEF\uFF0C\u770B\u5230\u7EEF\u6E05\u96C5\u5728\u65BD\u7CA5\uFF0C\u4ED6\u7591\u4E86\u4E00\u58F0\uFF0C\u6765\u5230\u7EEF\u6E05\u96C5\u9762\u524D\uFF0C\u201C\u8FD9\u4F4D\u592B\u4EBA\uFF0C\u4F60\u672C\u662F\u4E0D\u8BE5\u5B58\u5728\u8FD9\u4E16\u4E0A\u7684\u4EBA\uFF0C\u4E3A\u4F55\u8FD8\u597D\u597D\u7684\uFF0C\u5947\u54C9\uFF0C\u602A\u54C9\uFF01\u4E0D\u8FC7\u79EF\u5584\u6210\u5FB7\uFF0C\u5EF6\u7EED\u5BFF\u547D\uFF0C\u4E5F\u4E0D\u7B97\u5947\u4E8B\u4E86\u3002\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u624B\u4E2D\u62FF\u7740\u7684\u52FA\u5B50\u4E00\u4E0B\u5B50\u6389\u843D\u5728\u5730\uFF0C\u5979\u5168\u8EAB\u8DDF\u7740\u53D1\u6296\u3002</p>\n<p>\u201C\u592B\u4EBA\uFF0C\u592B\u4EBA\uFF0C\u60A8\u600E\u4E48\u4E86\uFF1F\u201D</p>\n<p>\u7B49\u7EEF\u6E05\u96C5\u518D\u6B21\u56DE\u795E\u7684\u65F6\u5019\uFF0C\u5979\u5DF2\u7ECF\u8EBA\u5728\u4E86\u5E8A\u4E0A\uFF0C\u5DEB\u5343\u9B45\u7D27\u7D27\u7684\u63E1\u7740\u5979\u7684\u624B\uFF0C\u773C\u4E2D\u5168\u662F\u62C5\u5FE7\u548C\u5FC3\u75BC\u3002</p>\n<p>\u4E4B\u540E\uFF0C\u7EEF\u6E05\u96C5\u53D1\u4E86\u75AF\u4F3C\u5F97\u5F00\u59CB\u627E\u83B2\u82B1\u697C\uFF0C\u751A\u81F3\u662F\u5DEB\u65CF\u7981\u5730\u3002</p>\n<p>\u53EF\u662F\u6240\u6709\u4EBA\u90FD\u544A\u8BC9\u5979\uFF0C\u6CA1\u6709\u4EC0\u4E48\u83B2\u82B1\u697C\u3002</p>\n<p>\u76F4\u5230\u6709\u4E00\u6B21\uFF0C\u5DEB\u5343\u9B45\u5916\u51FA\uFF0C\u7EEF\u6E05\u96C5\u5728\u6708\u5706\u4E4B\u591C\u6309\u7167\u68A6\u4E2D\u7684\u8BB0\u5FC6\u627E\u5230\u4E86\u7981\u5730\uFF0C\u5728\u5730\u9762\u4E0A\u627E\u5230\u4E86\u4E00\u6EE9\u8840\u8FF9\uFF0C\u90A3\u5E94\u8BE5\u662F\u5979\u6389\u843D\u83B2\u82B1\u697C\u7684\u5730\u65B9\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u5413\u7684\u5168\u8EAB\u53D1\u6296\u3002</p>\n<p>\u201C\u8FD9\u662F\u4F60\u7684\u91CD\u751F\uFF0C\u662F\u5DEB\u65CF\u5C11\u7237\u5229\u7528\u5BB6\u65CF\u79D8\u672F\uFF0C\u9006\u5929\u800C\u884C\uFF0C\u8BA9\u4F60\u4EEC\u91CD\u6765\u4E00\u4E16\u3002\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u56DE\u5934\u4E00\u770B\uFF0C\u7ADF\u7136\u662F\u90A3\u5929\u7684\u9053\u58EB\uFF0C\u201C\u5927\u5E08\uFF0C\u6211\u662F\u9B3C\u5417\uFF1F\u201D</p>\n<p>\u201C\u4E0D\uFF0C\u4F60\u662F\u4EBA\u3002\u201D</p>\n<p>\u201C\u90A3\u5DEB\u5343\u9B45\u5462\uFF1F\u201D</p>\n<p>\u201C\u4ED6\u4E5F\u662F\u4EBA\u3002\u201D</p>\n<p>\u201C\u8FD9\u5230\u5E95\u662F\u600E\u4E48\u4E00\u56DE\u4E8B\uFF1F\u201D</p>\n<p>\u201C\u524D\u751F\u4F60\u88AB\u7231\u60C5\u6240\u4F24\uFF0C\u8BEF\u4F1A\u91CD\u91CD\uFF0C\u540E\u4F60\u8EAB\u6B7B\uFF0C\u771F\u76F8\u5927\u767D\uFF0C\u5DEB\u5343\u9B45\u65BD\u5C55\u79D8\u672F\uFF0C\u4E3A\u4F60\u6B89\u60C5\uFF0C\u53D7\u5C3D\u767E\u5E74\u51C4\u82E6\uFF0C\u7B49\u4F60\u767E\u5E74\uFF0C\u624D\u6709\u8FD9\u4E00\u4E16\uFF0C\u592B\u4EBA\u56DE\u53BB\u5427\uFF01\u201D</p>\n<p>\u201C\u6211\u60F3\u77E5\u9053\u524D\u751F\u7684\u4E00\u5207\u3002\u201D</p>\n<p>\u201C\u524D\u5C18\u5F80\u4E8B\u7686\u4E3A\u8FC7\u5F80\uFF0C\u4F55\u5FC5\u6267\u7740\uFF0C\u6267\u7740\u4FBF\u662F\u6267\u5FF5\uFF0C\u653E\u4E0B\u624D\u80FD\u91CD\u751F\u3002\u201D</p>\n<p>\u4E4B\u540E\uFF0C\u9053\u58EB\u79BB\u53BB\uFF0C\u7981\u5730\u548C\u83B2\u82B1\u53F0\u4E5F\u5F7B\u5E95\u6D88\u5931\u4E86\uFF0C\u800C\u7EEF\u6E05\u96C5\u4E5F\u8DDF\u7740\u660F\u8FF7\u4E0D\u9192\u3002</p>\n<p>\u7B49\u5979\u518D\u6B21\u9192\u6765\u7684\u65F6\u5019\uFF0C\u53D1\u73B0\u5DEB\u5343\u9B45\u63E1\u4F4F\u5979\u7684\u624B\u5728\u54ED\u3002</p>\n<p>\u201C\u6E05\u6E05\uFF0C\u9192\u6765\u597D\u5417\uFF1F\u6211\u4E0D\u80FD\u6CA1\u6709\u4F60\uFF0C\u6E05\u6E05\u2026\u2026\u201D</p>\n<p>\u201C\u5343\u9B45\uFF01\u201D</p>\n<p>\u201C\u6E05\u6E05\u4F60\u7EC8\u4E8E\u9192\u4E86\uFF0C\u4F60\u53EF\u77E5\u9053\uFF0C\u4F60\u6709\u4E86\u5B69\u5B50\uFF0C\u6211\u4EEC\u7684\u5B69\u5B50\u3002\u201D</p>\n<p>\u201C\u662F\u5B69\u5B50\u5417\uFF1F\u201D</p>\n<p>\u5DEB\u5343\u9B45\u70B9\u4E86\u70B9\u5934\u3002</p>\n<p>\u7EEF\u6E05\u96C5\u53CC\u624B\u653E\u5728\u809A\u5B50\u4E0A\uFF0C\u60F3\u5230\u5927\u5E08\u6700\u540E\u8BF4\u7684\u8BDD\uFF0C\u518D\u770B\u5DEB\u5343\u9B45\u75DB\u82E6\u7684\u795E\u60C5\uFF0C\u5979\u8F7B\u8F7B\u7684\u7ED9\u4ED6\u5C06\u773C\u6CEA\u64E6\u53BB\u3002\u201C\u4EE5\u540E\u6211\u4EEC\u597D\u597D\u751F\u6D3B\u3002\u201D</p>\n<p>\u5DEB\u5343\u9B45\u543B\u4E86\u543B\u7EEF\u6E05\u96C5\u7684\u624B\u6307\uFF0C\u201C\u597D\u3002\u201D</p>\n<p>\u653E\u4E0B\u6267\u5FF5\u540E\u7684\u7EEF\u6E05\u96C5\u548C\u5DEB\u5343\u9B45\u4E5F\u8FC7\u8D77\u4E86\u4E0D\u7FA1\u9E33\u9E2F\u4E0D\u7FA1\u4ED9\u7684\u751F\u6D3B\u3002</p>\n<p>\u76F4\u5230\u4E24\u4EBA\u5BFF\u547D\u5C06\u5C3D\u7684\u65F6\u5019\uFF0C\u7EEF\u6E05\u96C5\u624D\u95EE\u5DEB\u5343\u9B45\uFF0C\u201C\u4F60\u8BB0\u5F97\u524D\u751F\u7684\u4E00\u5207\u5417\uFF1F\u201D</p>\n<p>\u201C\u8BB0\u5F97\uFF0C\u6E05\u6E05\uFF0C\u6211\u5BF9\u4E0D\u8D77\u4F60\u3002\u201D</p>\n<p>\u7EEF\u6E05\u96C5\u7B11\u7740\u6447\u4E86\u6447\u5934\uFF0C\u201C\u8FD9\u4E00\u751F\uFF0C\u4F60\u7ED9\u4E86\u6211\u5E78\u798F\uFF0C\u8FC7\u53BB\u7684\u4E8B\u60C5\u5C31\u662F\u524D\u5C18\u8FC7\u5F80\u4E86\u3002\u201D</p>\n<p>\u5979\u7EC8\u4E8E\u660E\u767D\u4E86\u5927\u5E08\u7684\u8BDD\uFF0C\u6267\u7740\u4FBF\u662F\u6267\u5FF5\uFF0C\u53EA\u4F1A\u56F0\u4F4F\u81EA\u5DF1\uFF0C\u82E5\u662F\u81EA\u5DF1\u4E00\u76F4\u6267\u7740\uFF0C\u672A\u5FC5\u80FD\u4EAB\u53D7\u8FD9\u51E0\u5341\u5E74\u6069\u7231\u7684\u751F\u6D3B\u3002</p>\n';

